import { StatusBar } from 'expo-status-bar';
import React, { useState } from 'react';
import { StyleSheet, Text, View } from 'react-native';
import { Slider, Input, CheckBox, Button, Icon } from 'react-native-elements';
import DateTimePickerModal from 'react-native-modal-datetime-picker';
import { color } from 'react-native-reanimated';
import shouldUseActivityState from 'react-native-screens'

// date observer weather
export default function AddObservation() {
  const [observation, setObservation] = useState({ species: '', place: '', datetime: {} })
  const [datePickerVisibility, setDatePickerVisibility] = useState(false)
  const [dateToPrint, setDateToPrint] = useState('')

  const handleConfirm = (date) => {
    console.log('DATE', typeof (date))

    setObservation({ ...observation, datetime: date })
    hideDatePicker()

  }

  const PrintableDate =()=> {
    if(observation.datetime === {}) {
      return "koira"
    }
    return(
      <Text>{observation.datetime.getHours}:{observation.datetime.getMinutes},  {observation.datetime.getDate}.{observation.datetime.getMonth + 1}.{observation.datetime.getFullYear}</Text>

    )
  }

  const hideDatePicker = () => {
    setDatePickerVisibility(false);
  };

  const showDatePicker = () => {
    setDatePickerVisibility(true)
  }

  return (
    <View style={styles.container}>
      <View style={styles.form}>
        <Text>Add obs</Text>

        <Input label="laji" name="species"
          onChangeText={text => setObservation({ ...observation, species: text })}></Input>

        <View style={{ flexDirection: 'row', alignItems: 'center' }}>
          <Input label="paikka" name="place"
            onChangeText={text => setObservation({ ...observation, place: text })}></Input>
          <Icon reverse name='map' iconStyle={{ padding: 10, borderRadius: 1, color: '#C7BABA' }} raised={true}></Icon>
        </View>

        <View style={{ flexDirection: 'row' }}>
          <Button buttonStyle={styles.timebutton} titleStyle={{ color: 'white' }} title=" aseta aika" onPress={showDatePicker}></Button>
          <PrintableDate></PrintableDate>
        </View>

        <DateTimePickerModal
          isVisible={datePickerVisibility} mode='datetime'
          onConfirm={handleConfirm} onCancel={hideDatePicker}></DateTimePickerModal>

      <View style={{flexDirection: 'row', width: '80%', alignItems: 'center'}}>
        <CheckBox title='uros' size={13}></CheckBox>
        <CheckBox title='naaras'  size={13}></CheckBox>
        <CheckBox title='group' size={13}></CheckBox>
        </View>
      </View>

      <Button buttonStyle={styles.button} titleStyle={{ color: 'white' }} title="tulosta" onPress={() => console.log(observation) || setObservation({})}></Button>

      {/*





      <Slider value={observation.amount}></Slider> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
  },
  button: {
    width: 300,
    margin: 10,
    borderRadius: 10,
    backgroundColor: '#C7BABA'
  },
  timebutton: {
    width: 150,
    margin: 10,
    borderRadius: 10,
    backgroundColor: '#C7BABA'

  },
  form: {
    width: '80%',
    alignItems: 'flex-start',
    padding: 20
  }
});
