    </View>

  )
}
// date observer weather
export default function AddObservation() {
  const [observation, setObservation] = useState({ species: '', place: '', datetime: {} })
  const [datePickerVisibility, setDatePickerVisibility] = useState(false)

  const handleConfirm = (date) => {
    //console.warn("A date has been picked: ", date);
    console.log('DATE', typeof (date))
    var keys = Object.keys(date)
    console.log("KEYKYEKEYEKY", keys)
    keys.forEach(element => {
      console.log(element)

    });

    setObservation({ ...observation, datetime: date })
    hideDatePicker()

  }

  const hideDatePicker = () => {
    setDatePickerVisibility(false);
  };

  const showDatePicker = () => {
    setDatePickerVisibility(true)
  }


  const handleName = (text) => {

  }

  return (
    <View style={styles.container}>
      <View style={styles.form}>
        <Text>Add obs</Text>

        <Input label="laji" name="species"
          onChangeText={text => setObservation({ ...observation, species: text })}></Input>

        <View style={{ flexDirection: 'row', alignItems: 'center' }}>
          <Input label="paikka" name="place"
            onChangeText={text => setObservation({ ...observation, place: text })}></Input>
          <Icon reverse name='map' iconStyle={{ padding: 10, borderRadius: 1, color: '#C7BABA' }} raised={true}></Icon>
        </View>

        <View style={{ flexDirection: 'row' }}>
          <Button buttonStyle={styles.timebutton} titleStyle={{ color: 'white' }} title=" aseta aika" onPress={showDatePicker}></Button>
          <Text>{}</Text>
        </View>

        <DateTimePickerModal
          isVisible={datePickerVisibility} mode='datetime'
          onConfirm={handleConfirm} onCancel={hideDatePicker}></DateTimePickerModal>
      </View>

      <Button buttonStyle={styles.button} titleStyle={{ color: 'white' }} title="tulosta" onPress={() => console.log(observation) || setObservation({})}></Button>

      {/*



      <CheckBox title='aurinkoinen'></CheckBox>
      <CheckBox title='lämmin'></CheckBox>
      <CheckBox title='pilviä ja lämmin'></CheckBox>
      <CheckBox title='sateinen'></CheckBox>

      <Slider value={observation.amount}></Slider> */}
    </View>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',
    alignItems: 'flex-start',
    justifyContent: 'flex-start',
  },
  button: {
    width: 300,
    margin: 10,
    borderRadius: 10,
    backgroundColor: '#C7BABA'
  },
  timebutton: {
    width: 150,
    margin: 10,
    borderRadius: 10,
    backgroundColor: '#C7BABA'

  },
  form: {
    width: '80%',
    alignItems: 'flex-start',
    padding: 20
  }
});
